//keyboard code by Ahmed
 module Keyboard =

    let mutable keysPressed = Set.empty //creates an empty set to store the pressed keys.
    let resetSet () = keysPressed <- Set.empty // reset allows the set to be emptied.
    let isPressed keyCode = Set.contains keyCode keysPressed // checks if the pressed keys stored in the set matches the keycode.

 
    let update (e : KeyboardEvent, pressed) = // Event is triggered when the keycode is pressed
      let keyCode = int e.keyCode //The keycode is stored as an int
      let When =  if pressed then Set.add else Set.remove //if the corresponding keycode is pressed.
      keysPressed <- When keyCode keysPressed //Store key code in keyspressed set.




      
